html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure,
footer, header, hgroup, menu, nav, section {
  display: block;
}

ol, ul {
  list-style: none;
}

blockquote, q {
  quotes: none;
}

blockquote:before, blockquote:after,
q:before, q:after {
  content: "";
  content: none;
}

/*
Color Wheel:
  Black: hsl(255, 0%, 0%)
  Dark Grey: hsl(237, 12%, 33%)
  Grey: hsl(240, 6%, 50%)
  White: hsl(255, 100%, 100%)
*/
/*
Important reminders:
1) using the "Null" keyword allows us to pass optional parameters to our mixins without bloating our code
2) using the "Null" keyword also means we don't have to worry about the order of our parameters.
3) mixins work best
*/
/*
Global Custom Properties:
  *COLORS*
    purple: hsl(238deg, 29%, 16%);
    orange: hsl(14deg, 88%, 65%);
    dark purple: hsl(240deg, 73%, 65%);
    black: hsl(255deg, 0%, 0%);
    dark Grey: hsl(237, 12%, 33%)
    grey: hsl(240, 6%, 50%)
    white: hsl(255, 100%, 100%)
  *FONT PROPERTIES*
    font-size: 1rem;
    font-weight: 400;
    font-family Arial sans-serif;
    line-height: 1.5;
  *lAYOUT PROPERTIES*
    max-width: 1200px;
    space: 15px;
    border-radius: 10px;
*/
:root {
  /*-----Color Custom Properties-----*/
  --clr-primary-color: hsl(266, 35%, 17%);
  --clr-accent-color: hsl(39, 72%, 82%);
  --clr-accent-color2: hsl(273, 73%, 47%);
  --clr-bg-main: hsl(266, 49%, 22%);
  --clr-bg-blanco: hsl(0, 0%, 100%);
  --clr-bg-stone: hsl(0, 0%, 85%);
  --clr-text-light: hsl(0, 0%, 100%);
  --clr-text-dark: hsl(255, 0%, 0%);
  /*-----Font Custom Properties-----*/
  --fp-size-main: 1rem;
  --fp-weight-light: 200;
  --fp-weight-semi-light: 300;
  --fp-weight-main: 400;
  --fp-weight-bold: 700;
  --fp-font-main: Ubuntu;
  --fp-font-secondary: Rokkitt;
  --fp-font-fallback: sans-serif;
  --fp-l-height-slight: 1.1;
  --fp-l-height-main: 1.3;
  --fp-l-height-large: 1.5;
  /*-----Layout Custom Properties-----*/
  --max-width: 1200px;
  --space: 15px;
  --curved-radius: 10px;
}

/*
Important reminders: 
1) only use functions for simple calculations we can use 
   instead of doing the math in our own head
*/
html,
body {
  font-family: var(--fp-font-main);
  line-height: 1.4;
}

.grid__container {
  display: grid;
  place-items: center;
}

.title__primary, .title__secondary, p {
  letter-spacing: 1px;
  word-spacing: 0.5px;
}

a {
  display: inline-block;
  text-decoration: none;
}

.title__secondary--dark, .title__secondary--projects, .title__secondary--skills {
  text-align: center;
}

/*-----BASE GLOBAL STYLES------*/
* {
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

#about,
#projects {
  scroll-margin-top: 100px;
}

body {
  background: radial-gradient(125% 125% at 50% 90%, #000000 40%, hsl(266, 35%, 17%) 100%);
}

.body__container {
  background-color: transparent;
  max-width: 1300px;
  margin-inline: auto;
}

main {
  overflow: hidden;
}

.video-container {
  position: fixed; /* or absolute, depending on your layout */
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1; /* This is crucial for placing the video behind other content */
}

#myVideo {
  min-width: 100%;
  min-height: 100%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

header {
  padding: 2.5rem 0;
  z-index: 1;
  width: 100vw;
}
@media (min-width: 1024px) {
  header {
    width: inherit;
    border-radius: 20px;
  }
}

footer {
  margin-block-start: 2rem;
  background-color: var(--clr-accent-color);
  color: var(--clr-primary-color);
  text-align: center;
  padding: 2rem;
  font-size: clamp(1rem, 2vw, 1.5rem);
  border-top-left-radius: 20px;
  border-top-right-radius: 20px;
  min-height: 100px;
}

.glass3d {
  --filter-glass3d: blur(10px) brightness(0.67) saturate(2.5);
  --color-glass3d: hsla(273, 43%, 47%, 0.1);
  --noise-glass3d: url("https://www.transparenttextures.com/patterns/egg-shell.png");
  position: relative;
  z-index: 0;
  box-shadow: 0 0 0.75px hsla(205, 20%, 10%, 0.2), 0.7px 0.8px 1.2px -0.4px hsla(205, 20%, 10%, 0.1), 1.3px 1.5px 2.2px -0.8px hsla(205, 20%, 10%, 0.1), 2.3px 2.6px 3.9px -1.2px hsla(205, 20%, 10%, 0.1), 3.9px 4.4px 6.6px -1.7px hsla(205, 20%, 10%, 0.1), 6.5px 7.2px 10.9px -2.1px hsla(205, 20%, 10%, 0.1), 8px 9px 14px -2.5px hsla(205, 20%, 10%, 0.2);
}

.glass3d::before {
  content: "";
  position: absolute;
  inset: 0;
  pointer-events: none;
  border-radius: 20px;
  overflow: hidden;
  z-index: 3;
  -webkit-backdrop-filter: var(--filter-glass3d);
  backdrop-filter: var(--filter-glass3d);
  background-color: var(--color-glass3d);
  background-image: var(--noise-glass3d);
  background-size: 100px;
  background-repeat: repeat;
}

.glass3d::after {
  content: "";
  position: absolute;
  inset: 0;
  pointer-events: none;
  border-radius: 20px;
  overflow: hidden;
  z-index: 5;
  box-shadow: inset 2px 2px 1px -3px hsla(205, 20%, 90%, 0.8), inset 4px 4px 2px -6px hsla(205, 20%, 90%, 0.3), inset 1.5px 1.5px 1.5px -0.75px hsla(205, 20%, 90%, 0.15), inset 1.5px 1.5px 0.25px hsla(205, 20%, 90%, 0.03), inset 0 0 0.25px 0.5px hsla(205, 20%, 90%, 0.03);
}

.glass3d > * {
  position: relative;
  z-index: 6;
}

@media (min-width: 768px) {
  .glass3d {
    z-index: 4;
  }
}
img {
  display: block;
  object-fit: cover;
}

img,
svg,
video {
  max-width: 100%;
  height: auto;
}

video {
  width: 100vw;
  height: auto;
  display: block;
  object-fit: cover;
}

.video-container video {
  max-width: unset;
}

/*visually hidden */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

.padB40 {
  padding-bottom: 40px;
}
@media (min-width: 768px) {
  .padB40 {
    padding-bottom: unset;
  }
}

/*----HEADING STYLES----*/
:is(h1, h2, h3, h4, h5, h6) {
  font-family: var(--fp-font-main);
  text-wrap: balance;
}

.title__primary {
  margin-top: 50px;
  font-size: clamp(40px, 12vw, 5rem);
  line-height: 1.2;
  font-weight: 400;
  color: var(--clr-text-light);
}
@media screen and (min-width: 600px) {
  .title__primary {
    margin-top: 90px;
  }
}
@media (min-width: 768px) {
  .title__primary {
    margin-top: unset;
    font-size: clamp(40px, 5vw, 5rem);
  }
}

.title__secondary {
  font-size: clamp(0.875rem, 4vw, 1.2rem);
  font-weight: 500;
  color: var(--clr-text-light);
}
.title__secondary--dark {
  font-size: clamp(26px, 7.5vw, 56px);
  font-weight: 700;
  color: var(--clr-text-light);
  margin-bottom: 12px;
}
@media (min-width: 768px) {
  .title__secondary--dark {
    text-align: left;
    margin-top: 30px;
    font-size: clamp(20px, 2.75vw, 40px);
  }
}
@media (min-width: 1024px) {
  .title__secondary--dark {
    margin-top: 50px;
  }
}
.title__secondary--projects {
  margin-bottom: 30px;
  font-size: clamp(26px, 6vw, 32px);
  font-weight: 700;
  color: var(--clr-text-light);
}
@media (min-width: 768px) {
  .title__secondary--projects {
    margin-bottom: unset;
    text-align: left;
    padding-left: 20px;
    padding-right: 18%;
  }
}
.title__secondary--skills {
  font-size: clamp(26px, 7.5vw, 32px);
  color: var(--clr-text-light);
  margin-bottom: 30px;
}
@media (min-width: 768px) {
  .title__secondary--skills {
    text-align: left;
  }
}

/*----CONTENT STYLES----*/
.content {
  font-size: 18px;
  font-weight: 800;
  color: var(--clr-text-color);
}
.content--about-me {
  font-size: clamp(16px, 5vw, 36px);
  line-height: 1.4;
  font-weight: 400;
  color: var(--clr-text-light);
}
@media (min-width: 768px) {
  .content--about-me {
    font-size: clamp(15px, 2vw, 28px);
  }
}
@media (min-width: 1024px) {
  .content--about-me {
    font-size: clamp(18px, 2vw, 28px);
  }
}
@media (prefers-color-scheme: dark) {
  .content--about-me {
    color: var(--clr-text-light);
  }
}
.content--stats {
  font-size: clamp(12px, 2vw, 28px);
  line-height: 1.2;
  font-weight: 400;
  color: var(--clr-text-light);
}
.content--large {
  font-size: clamp(3rem, 6.25vw, 5rem);
}

.subtitle {
  font-size: clamp(1.25rem, 6.5vw, 2.5rem);
  font-weight: 400;
  color: var(--clr-accent-color);
}
.subtitle--projects {
  border-bottom: 1px solid var(--clr-accent-color);
  font-size: clamp(0.875rem, 4vw, 1.125rem);
}
@media (min-width: 768px) {
  .subtitle {
    font-size: clamp(1.25rem, 4vw, 2.5rem);
  }
}

.container {
  padding-inline: 1.875rem;
}
@media (min-width: 768px) {
  .container {
    padding-inline: 50px;
  }
}
@media (min-width: 1024px) {
  .container {
    padding-inline: 60px;
  }
}

/*------FLEXBOX LAYOUT STYLES-----------*/
.flex {
  display: flex;
}

.flex__column {
  flex-direction: column;
}

.flex__row {
  flex-direction: row;
}
@media (min-width: 768px) {
  .flex__row--md {
    display: flex;
    flex-direction: row;
  }
}

.flex__start {
  justify-content: flex-start;
}
@media (min-width: 768px) {
  .flex__start {
    align-items: flex-start;
  }
}

.flex__end {
  justify-content: flex-end;
}

.flex__around {
  justify-content: space-around;
}

.flex__between {
  justify-content: space-between;
}

.flex__center {
  justify-content: center;
}

.flex__v--center {
  align-items: center;
}

.flex__wrap {
  flex-wrap: wrap;
}

.flex__nowrap {
  flex-wrap: nowrap;
}

.flex__grow {
  flex: 1;
}
@media (min-width: 768px) {
  .flex__grow--half_double {
    flex: 1.35;
  }
}
@media (min-width: 768px) {
  .flex__grow--double {
    flex: 1.75;
  }
}

.gap20 {
  gap: 20px;
}

/*------GRID LAYOUT STYLES-----------*/
.grid__container {
  min-height: 100vh;
}

.main__grid--container {
  min-height: 100vh;
  display: grid;
  grid-template-rows: 88px auto 1fr 86px;
  grid-template-areas: "grid-header" "grid-banner" "grid-main" "grid-footer";
}
@media (min-width: 768px) {
  .main__grid--container {
    grid-template-rows: 74px 650px 1fr;
    grid-template-columns: 50% 50%;
    grid-template-areas: "grid-header grid-header" "grid-banner grid-banner" "grid-main grid-main" "grid-footer grid-footer";
  }
}

.site_container {
  max-width: 1300px;
  margin-inline: auto;
  transform: translateY(-25px);
}

.grid_header {
  grid-area: grid-header;
  margin: 10px auto;
  position: sticky;
  top: 0;
  z-index: 1000;
}
@media (min-width: 768px) {
  .grid_header {
    width: 100%;
  }
}

.grid_banner {
  grid-area: grid-banner;
}
@media (min-width: 768px) {
  .grid_banner {
    margin-top: -72px;
  }
}

.grid_main {
  grid-area: grid-main;
}

.grid_nav {
  grid-area: grid-nav;
}

.grid_footer {
  grid-area: grid-footer;
}

/*------About Me Styles-----------*/
.about__me {
  padding-top: 20px;
  border-radius: 20px;
  overflow: hidden;
}
@media (prefers-color-scheme: dark) {
  .about__me {
    border: 2px solid var(--clr-bg-blanco);
  }
}
.about__me div:nth-of-type(1) {
  padding-inline: 25px;
}
@media (min-width: 768px) {
  .about__me {
    padding-top: unset;
    margin-top: 96px;
  }
}

.about__me--content {
  padding-bottom: 20px;
}

.header__logo--link-container {
  display: inline;
}

.header__nav--bar {
  display: none;
}
@media (min-width: 768px) {
  .header__nav--bar {
    display: block;
    width: 100%;
    height: inherit;
  }
}

.header__nav--wrapper {
  height: inherit;
  width: 100%;
}
@media (min-width: 768px) {
  .header__nav--wrapper {
    display: flex;
  }
}

.header--nav__link {
  padding: 0em 1em;
}

.nav__bar {
  z-index: 50;
  position: fixed;
  right: 0;
  width: 180px;
  margin-top: -1033px;
  border-bottom-left-radius: 10px;
  border-top-right-radius: 10px;
  transition: margin-top 0.7s ease-in;
  background-color: var(--clr-bg-stone);
}
@media (min-width: 768px) {
  .nav__bar {
    width: 200px;
  }
}
@media (min-width: 1024px) {
  .nav__bar {
    display: none;
  }
}

.show__nav {
  margin-top: 90px;
  transition: margin-top 0.5s ease-out;
}
@media (min-width: 1024px) {
  .show__nav {
    display: none;
  }
}

.nav__wrapper {
  padding-block: 1rem;
  border-radius: 0 0 0 25px;
  display: flex;
  flex-direction: column;
}
@media (min-width: 768px) {
  .nav__wrapper {
    padding-block: unset;
  }
}

.nav__item {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 0.5em 0 1em 0em;
}
#resume__desktop,
#resume__mobile {
  background-color: var(--clr-bg-blanco);
  color: var(--clr-text-dark);
  padding: 0.5em 1.75em;
  border-radius: 10px;
  transition: background-color 0.3s ease-in-out, color 0.3s ease-in-out;
}
#resume__desktop:hover,
#resume__mobile:hover {
  background-color: var(--clr-accent-color);
  color: var(--clr-primary-color);
}

.header--nav__link,
.nav__link {
  text-decoration: none;
  color: var(--clr-text-light);
  letter-spacing: 0.075rem;
}

@media (min-width: 1024px) {
  .header--nav__link {
    font-size: 1.125rem;
    padding: 0.5em 1em;
  }
}

.nav__link {
  color: var(--clr-primary-color);
}

.hamburger__menu {
  display: block;
  position: relative;
  bottom: 52px;
}
@media (min-width: 768px) {
  .hamburger__menu {
    display: none;
  }
}

.hamburger__menu {
  left: 8.5rem;
} /* base for smallest (136px) */
/* 360px breakpoint */
@media (min-width: 360px) {
  .hamburger__menu {
    left: 10.375rem;
  } /* 166px */
}
/* 375px breakpoint */
@media (min-width: 375px) {
  .hamburger__menu {
    left: 11.25rem;
  } /* 180px */
}
/* 414px breakpoint */
@media (min-width: 411px) {
  .hamburger__menu {
    left: 13.4375rem;
  } /* 215px */
}
/* 540px breakpoint */
@media (min-width: 540px) {
  .hamburger__menu {
    left: 20.4375rem;
  } /* 215px */
}
/* 768px and up */
@media (min-width: 768px) {
  .hamburger__menu {
    left: 32.5rem;
  } /* 520px */
}
.line {
  position: absolute;
  top: 48px;
  height: 3px;
  width: 35px;
  background: #fff;
  border-radius: 12px;
  transition: all 0.3s ease;
}
@media (min-width: 1024px) {
  .line {
    margin-left: 38px;
    display: none;
  }
}

.line1 {
  transform: translateY(-8px);
  width: 30px;
}

.line3 {
  transform: translateY(8px);
  width: 30px;
}

.change .line1 {
  transform: translateY(5px) rotate(-45deg) scale(1.2);
  margin-top: -6px;
}

.change .line2 {
  opacity: 0;
}

.change .line3 {
  transform: translateY(5px) rotate(45deg) scale(1.2);
  margin-top: -6px;
}

ul.card-container {
  list-style-type: none;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;
  width: 1200px;
}

.card {
  perspective: 1000px;
  min-width: 260px;
  position: relative;
  cursor: pointer;
}
@media (min-width: 768px) {
  .card {
    min-width: 500px;
  }
}

.card-content {
  position: relative;
  width: 100%;
  height: 100%;
  transform-style: preserve-3d;
  transition: transform 0.6s cubic-bezier(0.23, 1, 0.32, 1);
  border-radius: 24px;
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.1), 0 3px 10px rgba(0, 0, 0, 0.07);
  min-height: 480px;
  inset: 0;
  overflow: hidden;
}
@media (min-width: 768px) {
  .card-content {
    min-height: unset;
    inset: unset;
    display: grid;
    grid-template-columns: 300px 1fr;
  }
}

.card:hover .card-content {
  transform: rotateY(5deg) rotateX(5deg);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2), 0 5px 15px rgba(0, 0, 0, 0.1);
}

.card-image {
  position: relative;
  overflow: hidden;
  border-radius: 24px 24px 0 0;
  height: 100%;
}
@media (min-width: 768px) {
  .card-image {
    height: unset;
  }
}

.card-image-container {
  position: relative;
  width: 100%;
  height: 100%;
  overflow: hidden;
}

.card-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.8s cubic-bezier(0.165, 0.84, 0.44, 1);
}

.card:hover .card-image img {
  transform: scale(1.05);
}

.card-glow {
  position: absolute;
  width: 200%;
  height: 200%;
  background: radial-gradient(circle at 50% 50%, rgba(255, 255, 255, 0.25), transparent 50%);
  pointer-events: none;
  top: -50%;
  left: -50%;
  opacity: 0;
  transition: opacity 0.6s ease;
}

.card:hover .card-glow {
  opacity: 1;
}

.card-info {
  position: absolute;
  justify-content: flex-end;
  padding: 25px;
  display: flex;
  flex-direction: column;
  inset: 0;
}
.card-info::before {
  content: "";
  position: absolute;
  background: linear-gradient(to bottom, hsla(266, 49%, 22%, 0) 29%, hsla(266, 49%, 22%, 0.6) 56%, hsl(266, 49%, 22%) 100%);
  inset: 0;
}
@media (min-width: 768px) {
  .card-info {
    position: relative;
    justify-content: center;
    inset: unset;
  }
  .card-info::before {
    display: none;
  }
}

.card-name {
  margin: 0 0 6px;
  font-size: 24px;
  font-weight: 700;
  color: var(--clr-text-light);
  letter-spacing: -0.5px;
  z-index: 1;
}

.card-title {
  margin: 0 0 15px;
  font-size: 14px;
  color: var(--clr-text-light);
  font-weight: 600;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  z-index: 1;
}

.card-bio {
  margin: 0 0 20px;
  font-size: 15px;
  line-height: 1.5;
  color: var(--clr-text-light);
  z-index: 1;
}
@media (min-width: 768px) {
  .card-bio {
    margin: 0 0 40px;
  }
}

.card-actions {
  display: flex;
  gap: 12px;
  justify-content: flex-end;
}
@media (min-width: 768px) {
  .card-actions {
    flex-direction: column;
  }
}

.action-button {
  flex: 1;
  border: none;
  border-radius: 12px;
  padding: 12px;
  font-weight: 600;
  font-size: clamp(12px, 2vw, 16px);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.action-button svg {
  transition: transform 0.3s ease;
}

.action-button.primary {
  background: linear-gradient(135deg, hsl(273, 73%, 47%) 10%, hsl(266, 35%, 17%) 90%);
  color: white;
}

.action-button.secondary {
  background: hsl(39, 72%, 82%);
  color: var(--clr-primary-color);
  border: 1px solid hsl(266, 49%, 22%);
}

.action-button::before {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: all 0.6s ease;
}

.action-button:hover {
  transform: translateY(-3px);
  box-shadow: 0 5px 15px rgba(94, 96, 206, 0.2);
}

.action-button:active {
  transform: translateY(-1px);
}

.action-button:hover::before {
  left: 100%;
}

.action-button:hover svg {
  transform: scale(1.1);
}

.cta__contact {
  display: inline-block;
  background-color: var(--clr-bg-blanco);
  padding: 0.75rem 2rem;
  color: var(--clr-text-dark);
  font-size: clamp(18px, 3vw, 22px);
  font-weight: 700;
  border-radius: 10px;
  width: 100%;
  text-align: center;
  transition: background-color 0.3s ease;
  cursor: pointer;
}
@media (prefers-color-scheme: dark) {
  .cta__contact {
    background-color: var(--clr-primary-color);
    color: var(--clr-text-light);
    border: 2px solid var(--clr-text-light);
  }
}
.cta__contact:hover {
  background-color: var(--clr-accent-color);
}

.cta__project--container {
  margin-bottom: 20px;
  gap: 10px;
}
@media (min-width: 768px) {
  .cta__project--container {
    margin-bottom: unset;
    position: absolute;
    top: 0;
    left: 35%;
    width: 35%;
  }
}

.cta__project {
  width: 100%;
  padding-block: 0.65rem;
  border: 2px solid var(--clr-text-light);
  background-color: transparent;
  color: var(--clr-text-light);
  font-weight: 700;
  font-size: 1.125em;
  position: relative;
  cursor: pointer;
  transition: background-color 0.3s ease-in-out, color 0.3s ease-in-out;
}
.cta__project:hover {
  background-color: var(--clr-accent-color);
  color: var(--clr-primary-color);
}
.cta__project {
  transition: background-color color 0.3s ease;
}
.cta__project--active {
  background-color: var(--clr-bg-blanco);
  color: var(--clr-text-dark);
}
.cta__project:first-child {
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
}
.cta__project:last-child {
  border-top-right-radius: 10px;
  border-bottom-right-radius: 10px;
}

.cta__container--text {
  font-size: 1.5rem;
}

.slider__header {
  max-width: 320px;
}

.slider__container {
  margin-block: 3.125rem 4.375rem;
  position: relative;
}

.slider__cards {
  display: flex;
  justify-content: center;
  gap: 30px;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  scroll-behavior: smooth;
  overflow-x: scroll; /* Allows horizontal scrolling */
  -ms-overflow-style: none; /* Internet Explorer 10+ */
  scrollbar-width: none;
}
@media (min-width: 768px) {
  .slider__cards {
    justify-content: flex-start;
    flex-wrap: nowrap;
    padding: 20px;
  }
}

.banner__container {
  padding: 1rem 0 0;
}

.banner__content {
  padding-inline: 30px;
  width: 100%;
}
@media (min-width: 768px) {
  .banner__content {
    padding-left: 50px;
  }
}

.banner__img--container {
  width: 40%;
  position: relative;
  display: inline-block;
}

.banner__cta--container {
  padding: 2rem 0 4rem;
}

.banner__icons {
  width: clamp(30px, 8.5vw, 60px);
  height: clamp(30px, 8.5vw, 60px);
}
@media (min-width: 768px) {
  .banner__icons {
    width: clamp(40px, 5.2vw, 50px);
  }
}

.banner__icons:nth-of-type(2) {
  margin-inline: 0.75rem;
}

.banner__stats {
  border-radius: 20px;
  margin-inline: 50px;
}
@media (min-width: 768px) {
  .banner__stats {
    padding-left: 50px;
    padding-right: 50px;
  }
}
@media (min-width: 1024px) {
  .banner__stats {
    padding-left: 60px;
    padding-right: 60px;
  }
}

.img__header--logo {
  color: #ffffff;
  font-size: 1rem;
  font-weight: 700;
}

.img__banner {
  display: block;
  width: 100%;
  height: auto;
  object-fit: cover;
  filter: sepia(2%) contrast(3.5) brightness(1);
}

.img__about--me {
  background-color: var(--clr-bg-blanco);
  overflow: hidden;
}

.img__dt--overlay {
  position: relative;
}

.img__dt--overlay::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to bottom, hsla(266, 49%, 22%, 0.9), hsla(266, 49%, 22%, 0.8), hsla(266, 49%, 22%, 0.88), hsl(266, 49%, 22%) 100%);
}

.img__dt--overlay img {
  display: block;
  width: 100%; /* Adjust as needed */
  height: auto;
}

.img__banner--overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to right, hsl(266, 35%, 17%), hsla(266, 35%, 17%, 0.9), hsla(266, 35%, 17%, 0.8), hsla(266, 35%, 17%, 0.8));
}

.skills__container {
  margin-inline: 30px;
  padding: 20px;
}
.skills__container:first-child {
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
}
@media (prefers-color-scheme: dark) {
  .skills__container:first-child {
    border-top: solid;
    border-left: solid;
    border-right: solid;
  }
}
.skills__container:last-child {
  border-bottom-left-radius: 10px;
  border-bottom-right-radius: 10px;
}
@media (prefers-color-scheme: dark) {
  .skills__container:last-child {
    border-bottom: solid;
    border-left: solid;
    border-right: solid;
  }
}
@media (min-width: 768px) {
  .skills__container {
    margin-left: 50px;
    margin-block: 30px;
    padding: 25px;
  }
  .skills__container:first-child {
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
  }
  .skills__container:last-child {
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
  }
}
@media (min-width: 1024px) {
  .skills__container {
    margin-left: 60px;
    padding: 45px;
  }
}
@media (min-width: 1024px) and (prefers-color-scheme: dark) {
  .skills__container {
    border: 2px solid var(--clr-bg-blanco);
  }
}

.skills__icon {
  filter: drop-shadow(4px 4px 0px rgba(255, 255, 255, 0.5));
}

@media (min-width: 768px) {
  form {
    width: 80%;
    margin-inline: auto;
  }
}

.form__container {
  max-width: 86%;
  margin-inline: auto;
  margin-block-start: 2rem;
  padding-inline: 30px;
  padding-block: 65px;
  color: var(--clr-text-light);
}
@media (min-width: 768px) {
  .form__container {
    padding-block: 45px 80px;
    max-width: unset;
    margin-inline: unset;
  }
}
.form__container--title {
  font-size: 30px;
  line-height: 1.3;
  font-weight: 700;
  margin-bottom: 24px;
}
.form__container--subtitle {
  font-size: clamp(36px, 5vw, 40px);
  line-height: 1.2;
  font-weight: 700;
  color: var(--clr-accent-color);
  margin-bottom: 40px;
}
.form__container--cta {
  margin-top: 1.5rem;
  padding-block: 14px;
  border-radius: 10px;
  width: 100%;
  cursor: pointer;
}
.form__container--cta:focus-visible {
  outline: 4px dotted var(--clr-accent-color);
  outline-offset: 8px;
}
.form__container--cta {
  font-size: 24px;
  font-weight: 700;
  color: var(--clr-text-dark);
}
@media (min-width: 768px) {
  .form__container--cta {
    width: 85%;
    align-self: flex-end;
    padding-block: 20px;
  }
}
@media (min-width: 1024px) {
  .form__container--cta {
    width: 60%;
    max-width: 60%;
  }
}
@media (prefers-color-scheme: dark) {
  .form__container--cta {
    background-color: transparent;
    color: var(--clr-text-light);
    border: 4px solid var(--clr-text-light);
    transition: background-color 0.3s ease-in-out, color 0.3s ease-in-out;
  }
  .form__container--cta:hover {
    background-color: var(--clr-accent-color);
    color: var(--clr-primary-color);
  }
}

.input__field,
.textarea__field {
  position: relative;
}

.input__field input {
  height: 60px;
  width: 100%;
  font-size: 18px;
  padding-inline: 5px;
  border: none;
  border-bottom: 4px solid var(--clr-bg-blanco);
  outline: none;
  background-color: transparent;
  margin-bottom: 3rem;
  color: var(--clr-text-light);
}
.input__field input:focus {
  border-color: var(--clr-accent-color);
}

input:-webkit-autofill,
input:-webkit-autofill:hover,
input:-webkit-autofill:focus,
input:-webkit-autofill:active {
  -webkit-background-clip: text;
  -webkit-text-fill-color: var(--clr-text-light) !important;
}

input:focus ~ label,
input:valid ~ label {
  top: -10px;
  font-size: 16px;
  color: var(--clr-accent-color);
}

.input__field label {
  position: absolute;
  top: 30%;
  left: 5px;
  transform: translateY(-50%);
  font-size: 18px;
  color: var(--clr-text-light);
  pointer-events: none;
  transition: all 0.3s ease;
}

.textarea__field textarea {
  height: 120px;
  width: 100%;
  background-color: transparent;
  border: 4px solid var(--clr-bg-blanco);
  color: var(--clr-text-light);
  border-radius: 10px;
}

.textarea__field label {
  margin-bottom: 1.5rem;
  position: absolute;
  top: 20%;
  left: 12px;
  transform: translateY(-50%);
  font-size: 18px;
  color: var(--clr-text-light);
  pointer-events: none;
  transition: all 0.3s ease;
  border: none;
}

textarea:focus ~ label {
  top: -16px;
  left: 6px;
  font-size: 16px;
  color: var(--clr-accent-color);
}

.hide--mobile {
  display: none;
}
@media (min-width: 768px) {
  .hide {
    display: none;
  }
}
.hide--main-nav {
  display: none;
}

@media (min-width: 768px) {
  .show {
    display: block;
  }
}
@media (min-width: 768px) {
  .show--flex {
    display: flex;
  }
}

