// **************************************
// MIXINS
// **************************************

/*
Important reminders:
1) using the "Null" keyword allows us to pass optional parameters to our mixins without bloating our code
2) using the "Null" keyword also means we don't have to worry about the order of our parameters.
3) mixins work best
*/

@use "./breakpoints" as *;
@use "sass:map";

// Text properties
@mixin text-prop($size, $l-height: null, $f-weight: null, $color: null) {
  font-size: $size;
  line-height: $l-height;
  font-weight: $f-weight;
  color: $color;
}

//Create a flex container
@mixin flex_me($dir: null, $wrap: null, $just: null, $vertAlign: null) {
  display: flex;
  flex-direction: $dir;
  align-items: $vertAlign;
  justify-content: $just;
}

// Media Query
@mixin mq($break) {
  $value: map.get($breakpoints, $break);
  $sm: map.get($breakpoints, "sm");

  @if $value < $sm {
    @media (max-width: $value) {
      @content;
    }
  } @else {
    @media (min-width: $value) {
      @content;
    }
  }
}

sources.sass\\utilities\\_globals.scss:
// **************************************
// GLOBAL SCSS VARIABLES
// **************************************
@use "./colors" as *;
@use "./breakpoints" as *;
@use "./fonts" as *;
@use "./mixins" as *;


/*
Global Custom Properties:
  *COLORS*
    purple: hsl(238deg, 29%, 16%);
    orange: hsl(14deg, 88%, 65%);
    dark purple: hsl(240deg, 73%, 65%);
    black: hsl(255deg, 0%, 0%);
    dark Grey: hsl(237, 12%, 33%)
    grey: hsl(240, 6%, 50%)
    white: hsl(255, 100%, 100%)
  *FONT PROPERTIES*
    font-size: 1rem;
    font-weight: 400;
    font-family Arial sans-serif;
    line-height: 1.5;
  *lAYOUT PROPERTIES*
    max-width: 1200px;
    space: 15px;
    border-radius: 10px;
*/

:root {
  /*-----Color Custom Properties-----*/
  @each $color, $shades in $colors {
    @each $shade, $value in $shades {
      --clr-#{$color}-#{$shade}: #{$value};
    }
  }

  /*-----Font Custom Properties-----*/
  @each $font, $props in $font-love {
    @each $prop, $value in $props {
      --fp-#{$font}-#{$prop}: #{$value};
    }
  }

  /*-----Layout Custom Properties-----*/
  --max-width: 1200px;
  --space: 15px;
  --curved-radius: 10px;
}

sources.sass\\utilities\\_functions.scss:
// **************************************
//FUNCTIONS
// **************************************

/*
Important reminders: 
1) only use functions for simple calculations we can use 
   instead of doing the math in our own head
*/

@use "../utilities/globals" as *;
//convert pixels to rem value
$base-font-size: 1rem;
@function px-to-rems($target, $context: $base-font-size) {
  @return calc($target / $context) * 1rem;
}

//convert pixels to percentage value
@function px-to-pc($target, $context: $max-width) {
  @return calc($target / $context) * 100%;
}

