}

sources.sass\\base\\_base.scss:
// **************************************
// BASE STYLES
// **************************************

@use "../utilities" as *;

/*-----BASE GLOBAL STYLES------*/
* {
  box-sizing: border-box;
}

html {
   scroll-behavior: smooth;
}

#about,
#projects{
  scroll-margin-top: 100px;
}


html,
body {
  @extend %body-properties;
  // max-width: 1300px;
  // margin-inline: auto;
}

body {
  background: radial-gradient(125% 125% at 50% 90%, #000000 40%, hsl(266, 35%, 17%) 100%)
}

.body__container {
  background-color: transparent;
      max-width: 1300px;
  margin-inline: auto;
}


main {
overflow: hidden;
}

.video-container {
  position: fixed; /* or absolute, depending on your layout */
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: -1; /* This is crucial for placing the video behind other content */
}

#myVideo {
  min-width: 100%;
  min-height: 100%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

header {
  padding: 2.5rem 0;
  z-index: 1;
  width: 100vw;
  @include mq('md') {


  }
  @include mq('lg') {
    width: inherit;
    border-radius: 20px;
  }
}

footer {
  margin-block-start: 2rem;
  background-color: var(--clr-accent-color);
  color: var(--clr-primary-color);
  text-align: center;
  padding: 2rem;
  font-size: clamp(1rem, 2vw, 1.5rem);
  border-top-left-radius: 20px;
  border-top-right-radius: 20px;
  min-height: 100px;
}


 .glass3d {
  --filter-glass3d: blur(10px) brightness(0.67) saturate(2.5);
  --color-glass3d: hsla(273, 43%, 47%, 0.1);
  --noise-glass3d: url("https://www.transparenttextures.com/patterns/egg-shell.png");

  position: relative;
  z-index: 0;
  box-shadow:
    0 0 0.75px hsl(205 20% 10% / 0.2),
    0.7px 0.8px 1.2px -0.4px hsl(205 20% 10% / 0.1),
    1.3px 1.5px 2.2px -0.8px hsl(205 20% 10% / 0.1),
    2.3px 2.6px 3.9px -1.2px hsl(205 20% 10% / 0.1),
    3.9px 4.4px 6.6px -1.7px hsl(205 20% 10% / 0.1),
    6.5px 7.2px 10.9px -2.1px hsl(205 20% 10% / 0.1),
    8px 9px 14px -2.5px hsl(205 20% 10% / 0.2);
}


  .glass3d::before {
  content: "";
  position: absolute;
  inset: 0;
  pointer-events: none;
  border-radius: 20px;
  overflow: hidden;
  z-index: 3;
  -webkit-backdrop-filter: var(--filter-glass3d);
  backdrop-filter: var(--filter-glass3d);
  background-color: var(--color-glass3d);
  background-image: var(--noise-glass3d);
  background-size: 100px;
  background-repeat: repeat;
}

.glass3d::after {
  content: "";
  position: absolute;
  inset: 0;
  pointer-events: none;
  border-radius: 20px;
  overflow: hidden;
  z-index: 5;
  box-shadow:
    inset 2px 2px 1px -3px hsl(205 20% 90% / 0.8),
    inset 4px 4px 2px -6px hsl(205 20% 90% / 0.3),
    inset 1.5px 1.5px 1.5px -0.75px hsl(205 20% 90% / 0.15),
    inset 1.5px 1.5px 0.25px hsl(205 20% 90% / 0.03),
    inset 0 0 0.25px 0.5px hsl(205 20% 90% / 0.03);
}

.glass3d > * {
  position: relative;
  z-index: 6;
}

 @include mq("md") {
 .glass3d {
  z-index: 4;
 }

}


img {
  display: block;
  object-fit: cover;
}

img,
svg,
video {
  max-width: 100%;
  height: auto;
}

video {
  width: 100vw;
  height: auto;
  display: block;
  object-fit: cover;
}

.video-container video {
  max-width: unset
}

p {
  @extend %text-modules;
}

a {
  @extend %link-properties;
}

/*visually hidden */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

.padB40 {
  padding-bottom: px-to-rems(40px);
  @include mq('md') {
    padding-bottom: unset;
  }
}

sources.sass\\base\\_typography.scss:
// **************************************
// TYPOGRAPHY STYLES
// **************************************
@use "../utilities" as *;

/*----HEADING STYLES----*/
:is(h1, h2, h3, h4, h5, h6) {
  font-family: var(--fp-font-main);
  text-wrap: balance;
}

// h1 styles
.title__primary {
  @extend %text-modules;
  margin-top: px-to-rems(50px);
  @include text-prop(
    $size: clamp(px-to-rems(40px), 12vw, 5rem),
    $f-weight: 400,
    $l-height: 1.2,
    $color: var(--clr-text-light)
  );
  @media screen and (min-width: 600px) {
    margin-top: px-to-rems(90px);
  }
  @include mq("md") {
    margin-top: unset;
    @include text-prop(
      $size: clamp(px-to-rems(40px), 5vw, 5rem),
    )
  }
}

// h2 styles
.title__secondary {
  @include text-prop(
    $size: clamp(0.875rem, 4vw, 1.2rem),
    $f-weight: 500,
    $color: var(--clr-text-light)
  );
  @extend %text-modules;
  &--dark {
    @extend %center-text;
    @include text-prop(
      $size: clamp(px-to-rems(26px), 7.5vw, px-to-rems(56px)),
      $f-weight: 700,
      $color: var(--clr-text-light)
    );
   margin-bottom: px-to-rems(12px);
   @include mq("md") {
    text-align: left;
    margin-top: px-to-rems(30px);
    @include text-prop(
      $size: clamp(px-to-rems(20px), 2.75vw, px-to-rems(40px)),
    );
  }
   @include mq("lg") {
    margin-top: px-to-rems(50px);

   }
  }

  &--projects {
    margin-bottom: px-to-rems(30px);
    @include text-prop(
      $size: clamp(px-to-rems(26px), 6vw, px-to-rems(32px)),
      $f-weight: 700,
      $color: var(--clr-text-light)
    );
    @extend %center-text;
    @include mq("md") {
      margin-bottom: unset;
      text-align: left;
      padding-left: 20px;
      padding-right: 18%;
    }
  }

  &--skills {
    @include text-prop(
      $size: clamp(px-to-rems(26px), 7.5vw, px-to-rems(32px)),
      $color: var(--clr-text-light)
    );
    @extend %center-text;
    margin-bottom: px-to-rems(30px);
    @include mq("md") {
      text-align: left;
    }

  }
}

/*----CONTENT STYLES----*/
// p styles
.content {
  @include text-prop(
    $size: px-to-rems(18px),
    $f-weight: 800,
    $color: var(--clr-text-color)
  );

  &--about-me {
    @include text-prop(
      $size: clamp(px-to-rems(16px), 5vw, px-to-rems(36px)),
      $l-height: 1.4,
      $f-weight: 400,
      $color: var(--clr-text-light)
    );
    @include mq("md") {
      @include text-prop(
        $size: clamp(px-to-rems(15px), 2vw, px-to-rems(28px)),
      );
    }
    @include mq("lg") {
      @include text-prop(
        $size: clamp(px-to-rems(18px), 2vw, px-to-rems(28px)),
      );
    }
     @media (prefers-color-scheme: dark) {
      color: var(--clr-text-light);
     }
  }


  &--stats {
    @include text-prop(
      $size: clamp(px-to-rems(12px), 2vw, px-to-rems(28px)),
      $l-height: 1.2,
      $f-weight: 400,
      $color: var(--clr-text-light)
    );
  }

  &--large {
    font-size: clamp(3rem, 6.25vw, 5rem);
  }
}

.subtitle {
  @include text-prop(
    $size: clamp(1.25rem, 6.5vw, 2.5rem),
    $f-weight: 400,
    $color: var(--clr-accent-color)
  );


  &--projects {
    border-bottom: 1px solid var(--clr-accent-color);
    @include text-prop($size: clamp(0.875rem, 4vw, 1.125rem),)
  }

  @include mq("md") {
    @include text-prop(
      $size: clamp(1.25rem, 4vw, 2.5rem),
    )
  }

}

sources.sass\\base\\_layout.scss:
// **************************************
// LAYOUT STYLES
// **************************************

@use "../utilities" as *;

.container {
  padding-inline: 1.875rem;

  @include mq("md") {
    padding-inline: px-to-rems(50px);
  }
  @include mq("lg") {
    padding-inline: px-to-rems(60px);
  }
}

/*------FLEXBOX LAYOUT STYLES-----------*/

.flex {
  display: flex;
}

.flex__column {
  flex-direction: column;
}

.flex__row {
  flex-direction: row;
  &--md {
    @include mq('md') {
      display: flex;
      flex-direction: row;
    }
  }
}

.flex__start {
  justify-content: flex-start;
  @include mq('md') {
    align-items: flex-start;
  }
}

.flex__end {
  justify-content: flex-end;
}

.flex__around {
  justify-content: space-around;
}

.flex__between {
  justify-content: space-between;
}

.flex__center {
  justify-content: center;
}

.flex__v--center {
  align-items: center;;
}

.flex__wrap {
  flex-wrap: wrap;
}

.flex__nowrap {
  flex-wrap: nowrap;
}

.flex__grow {
  flex: 1;
  &--half_double {
    @include mq('md') {
      flex: 1.35;
    }
  }
  &--double {
    @include mq('md') {
      flex: 1.75;
    }
  }
}

.gap20 {
  gap: px-to-rems(20px);
}

/*------GRID LAYOUT STYLES-----------*/

.grid__container {
  @extend %ctr-grid-content;
  min-height: 100vh;
}

.main__grid--container {
  min-height: 100vh;
  display: grid;
  grid-template-rows: px-to-rems(88px) auto 1fr px-to-rems(86px);
  grid-template-areas:
    "grid-header"
    "grid-banner"
    "grid-main"
    "grid-footer";
    @include mq('md') {
      grid-template-rows: px-to-rems(74px) px-to-rems(650px) 1fr;
      grid-template-columns: 50% 50%;
      grid-template-areas:
      "grid-header grid-header"
      "grid-banner grid-banner"
      "grid-main grid-main"
      "grid-footer grid-footer";
    }
}

.site_container {
   max-width: 1300px;
   margin-inline: auto;
   transform: translateY(-25px);
}

.grid_header {
  grid-area: grid-header;
  margin: 10px auto;
   position: sticky;
  top: 0;
  z-index: 1000;
  @include mq('md') {
    width: 100%;
  }
}

.grid_banner {
  grid-area: grid-banner;
  @include mq('md') {
    margin-top: px-to-rems(-72px);
  }
}

.grid_main {
  grid-area: grid-main;
}
.grid_nav {
  grid-area: grid-nav;
}

.grid_footer {
  grid-area: grid-footer;
}
/*------About Me Styles-----------*/
.about__me {
  padding-top: px-to-rems(20px);
  border-radius: 20px;
  overflow: hidden;
  @media (prefers-color-scheme: dark) {
    border: 2px solid var(--clr-bg-blanco);
  }

  div:nth-of-type(1) {
   padding-inline: px-to-rems(25px);

  }
  @include mq('md') {
  padding-top: unset;
