  .hamburger__menu { left: 11.25rem; } /* 180px */
}

/* 414px breakpoint */
@media (min-width: 411px) {
  .hamburger__menu { left: 13.4375rem; } /* 215px */
}
/* 540px breakpoint */
@media (min-width: 540px) {
  .hamburger__menu { left: 20.4375rem; } /* 215px */
}

/* 768px and up */
@media (min-width: 768px) {
  .hamburger__menu { left: 32.5rem; } /* 520px */
}


//hamburger white line styles for mobile and tablet but are not displayed in desktop view
    //the 3 horizontal hamburger lines
    .line{
    position: absolute;
    top: 48px;
    height: 3px;
    width: 35px;
    background: #fff;
    border-radius: 12px;
    transition: all 0.3s ease;
    @include mq('lg'){
      margin-left: 38px;
      display: none;
    }
  }

  //The first horizontal hamburger line
  .line1{
    transform:translateY(-8px);
    width: 30px;
  }

  //The third horizontal hamburger line
  .line3{
    transform:translateY(8px);
    width: 30px;
  }

  //when clicked the 1st line turns and shifts clockwise
  .change .line1{
    transform: translateY(5px) rotate(-45deg) scale(1.2);
    margin-top: -6px;
  }

  //when clicked the second hamburger line disappears
  .change .line2{
    opacity: 0;
  }

  //when clicked the 3rd line turns and shifts clockwise forming the X look
  .change .line3{
    transform: translateY(5px) rotate(45deg) scale(1.2);
    margin-top: -6px;
  }

