// **************************************
// STATE STYLES
// **************************************
@use "../utilities" as *;

.hide {
  &--mobile {
    display: none;
  }
  @include mq('md') {
    display: none;
  }
  &--main-nav {
    display: none;
  }
}

.show {
  @include mq('md') {
    display: block;
  }
  &--flex {
    @include mq('md') {
      display: flex;
    }
  }
}
